hardcodedTailwindClasses.classes:
px-4 py-2 font-medium transition-colors ${
              activePanel === tab.key
                ? 'text-blue-400 border-b-2 border-blue-400'
                : 'text-gray-400 hover:text-gray-200'
            }

flex-1 py-2 px-4 rounded text-sm font-medium transition-colors ${
              viewMode === 'map'
                ? 'bg-blue-600 text-white'
                : 'text-gray-300 hover:text-white hover:bg-gray-600'
            }

flex-1 py-2 px-4 rounded text-sm font-medium transition-colors ${
              viewMode === 'input'
                ? 'bg-blue-600 text-white'
                : 'text-gray-300 hover:text-white hover:bg-gray-600'
            }

px-3 py-1 rounded text-sm ${
                  !useMapSelection
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                }

px-3 py-1 rounded text-sm ${
                  useMapSelection
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                }

px-4 py-2 rounded text-sm font-medium transition-colors ${
            currentView === 'overview'
              ? 'bg-blue-600 text-white'
              : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
          }

px-4 py-2 rounded text-sm font-medium transition-colors ${
              currentView === 'movement'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
            }

px-4 py-2 rounded font-medium ${
                    isMoving
                      ? 'bg-gray-600 text-gray-400 cursor-not-allowed'
                      : 'bg-green-600 text-white hover:bg-green-700'
                  }

p-3 rounded border cursor-pointer transition-all ${
                      selectedFleet?._id === fleet._id
                        ? 'border-blue-500 bg-blue-900/30'
                        : 'border-gray-600 bg-gray-700/50 hover:bg-gray-700'
                    }

px-2 py-1 rounded text-xs font-medium ${
                        fleet.status === 'stationed' ? 'bg-green-900/50 text-green-300' :
                        fleet.status === 'moving' ? 'bg-yellow-900/50 text-yellow-300' :
                        'bg-red-900/50 text-red-300'
                      }

px-4 py-2 font-medium transition-colors ${
              activeTab === 'overview'
                ? 'text-blue-400 border-b-2 border-blue-400'
                : 'text-gray-400 hover:text-gray-200'
            }

px-4 py-2 font-medium transition-colors ${
              activeTab === 'detail' && selectedBase
                ? 'text-blue-400 border-b-2 border-blue-400'
                : 'text-gray-400 hover:text-gray-200 disabled:opacity-50 disabled:cursor-not-allowed'
            }

px-3 py-1 rounded text-sm font-medium transition-colors ${
                        canStart && !loading
                          ? "bg-blue-600 hover:bg-blue-700 text-white"
                          : "bg-gray-600 text-gray-400 cursor-not-allowed"
                      }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'map'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'territories'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'explore'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'colonize'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

w-full mt-3 py-3 px-4 rounded-md font-medium transition-colors ${
                        canAffordColonization() && colonyName.trim()
                          ? 'bg-green-600 hover:bg-green-700 text-white'
                          : 'bg-gray-600 text-gray-400 cursor-not-allowed'
                      }

px-3 py-2 text-sm font-medium rounded-md transition-colors ${
              active === t.key
                ? 'bg-blue-600 text-white'
                : 'text-gray-300 hover:text-white hover:bg-gray-600'
            }

px-4 py-2 text-sm font-medium rounded-t-lg transition-colors ${
          isActive
            ? 'bg-blue-600 text-white border-b-2 border-blue-400'
            : 'bg-gray-700 text-gray-300 hover:text-white hover:bg-gray-600'
        }

p-3 rounded cursor-pointer transition-colors ${
                        selectedMessage?._id === message._id
                          ? 'bg-blue-900/50 border border-blue-700'
                          : 'bg-gray-800 hover:bg-gray-700'
                      }

text-xs px-2 py-1 rounded ${
                            deleteConfirm === message._id
                              ? 'bg-red-600 text-white'
                              : 'text-gray-400 hover:text-red-400'
                          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'available'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'active'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

flex-1 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
            activeTab === 'completed'
              ? 'bg-blue-600 text-white'
              : 'text-gray-300 hover:text-white hover:bg-gray-600'
          }

p-4 rounded-lg border ${
                          canAfford ? 'border-gray-600 bg-gray-700' : 'border-red-600 bg-red-900/20'
                        }

text-xs px-2 py-1 rounded ${
                            canAfford ? 'bg-yellow-600 text-white' : 'bg-red-600 text-white'
                          }

w-full mt-3 py-2 px-4 rounded-md text-sm font-medium transition-colors ${
                            canAfford
                              ? 'bg-blue-600 hover:bg-blue-700 text-white'
                              : 'bg-gray-600 text-gray-400 cursor-not-allowed'
                          }

text-xs px-3 py-1 rounded ${
              loading || totals.totalCredits <= 0
                ? "bg-gray-600 text-gray-400 cursor-not-allowed"
                : "bg-blue-600 hover:bg-blue-700 text-white"
            }

w-24 px-2 py-1 rounded text-right font-mono bg-gray-800 border ${
                        canStart && !loading
                          ? "border-gray-600 text-white"
                          : "border-gray-700 text-gray-400 cursor-not-allowed"
                      }

px-4 py-2 rounded-md text-sm font-semibold border border-gray-700 ${
        tab === value ? 'bg-gray-700 text-empire-gold' : 'text-gray-300 hover:text-white hover:bg-gray-700'
      }

w-full text-left px-3 py-2 rounded text-sm font-semibold ${
                      active ? 'bg-gray-700 text-empire-gold' : 'text-gray-300 hover:text-white hover:bg-gray-700'
                    }

w-full text-left px-3 py-2 rounded text-sm font-semibold ${
                        active ? 'bg-gray-700 text-empire-gold' : 'text-gray-300 hover:text-white hover:bg-gray-700'
                      }

w-full text-left px-3 py-2 rounded text-sm font-semibold ${
                      active ? 'bg-gray-700 text-empire-gold' : 'text-gray-300 hover:text-white hover:bg-gray-700'
                    }

w-full text-left px-3 py-2 rounded text-sm font-semibold ${
                        active ? 'bg-gray-700 text-empire-gold' : 'text-gray-300 hover:text-white hover:bg-gray-700'
                      }

flex items-center justify-between px-4 py-2 text-sm font-medium transition-all duration-300 ease-in-out
${isSuccess ? 'bg-green-600' : ''}
${isWarning ? 'bg-yellow-600' : ''}
${isError ? 'bg-red-600' : ''}

px-4 py-2 rounded transition-colors ${
                  variant === 'danger' ? 'bg-red-600 hover:bg-red-700' : 'bg-blue-600 hover:bg-blue-700'
                } text-white

relative max-w-sm w-full rounded-lg shadow-lg p-4 flex items-center space-x-3
animate-in slide-in-from-right duration-300
${toast.type === 'success' ? 'bg-green-600 text-white' : ''}
${toast.type === 'error' ? 'bg-red-600 text-white' : ''}
${toast.type === 'warning' ? 'bg-yellow-600 text-white' : ''}
${toast.type === 'info' ? 'bg-blue-600 text-white' : ''}

px-4 py-2 rounded text-sm font-medium transition-colors ${
            isChecking || state.checkingForUpdate
              ? 'bg-gray-600 text-gray-400 cursor-not-allowed'
              : 'bg-blue-600 hover:bg-blue-500 text-white'
          }
